function selectTargetFromTiku(title) {
    console.log('title is ', title)
    let returnObj = {}
    tiku.forEach(ele => {
        if (ele.title === title) {
            console.log(ele)
            returnObj = ele
        }
    })
    return returnObj
}

function transClickAnswer(array, optionArray) {
    var selectAnswerArray = []
    console.log('array',array)
    console.log('optionArray',optionArray)
    array.forEach(ele => {
        Object.keys(optionArray).forEach(key => {
            
            if (ele.replace(' ','') === optionArray[key]) {
                selectAnswerArray.push(key)
            }
        })
    })
    return selectAnswerArray
}

